import React, { useEffect, useState } from 'react';
import styled from 'styled-components';

const VideosContainer = styled.div`
	display: flex;
    flex-flow: row nowrap;
    overflow-x: auto;
    margin: 0px -1rem;
    padding: 0px 1rem;
`;

const Video = styled.div`
	padding: 1rem;
	background-image: url(${props => props.img});
	margin-right: 1rem;
	width: 283px;
	height: 160px;
	border-radius: 6px;
	display: inline-flex;
`;

function Videos({ id }) {
	const [videos, setVideos] = useState([]);

	useEffect(() => {
		fetch(`https://dapi.cms.mlbinfra.com/v2/content/en-us/videos?tags.slug=playerid-${id}`)
			.then(response => response.json())
			.then(data => {
				const videos = data.items;
				setVideos([...videos]);
			});
	}, [videos]);

	const vids = videos.map((vid) => {
		const src = vid.thumbnail.templateUrl.replace('{formatInstructions}', 't_16x9/t_w640');
		return (
			<Video img={src}>
				play
			</Video>
		)
	})

	return (
		<VideosContainer>
			{vids}
		</VideosContainer>
	);
}

export default Videos;
