import React, { useState } from 'react';
import ReactDOM from 'react-dom';
import styled from 'styled-components';

const Dialog = styled.dialog`
    width: 100vw;
    height: 100vh;
    z-index: 10000;
    position: fixed;
    top: 0;
    border: none;
    padding: 0rem;

    &.open {
        display: flex;
        flex-flow: row wrap;
        justify-content: center;
        align-items: flex-start;
    }
`;

const Hero = styled.img``;

function Portal({ portalState, setPortalState, portalContext, setPortalContext }) {

    function handleClose() {
        setPortalState(false);
    }

    const { name, id } = portalContext;

  return ReactDOM.createPortal(
    <Dialog open={portalState}>
        {portalState && <Hero src={`https://img.mlbstatic.com/mlb-photos/image/upload/w_1000,q_100/v1/people/${id}/action/hero/current`} />}
        <button onClick={handleClose}>Close</button>
    </Dialog>,
    document.getElementById('portal-root')
  );
}

export default Portal;
