import React, { useState, useEffect } from 'react';
import styled from 'styled-components';

const CardContainer = styled.div`
  display: inline-block;
  width: 90vw;
  max-width: 400px;
  height: 70vh;
  background: #FFFFFF;
  padding-bottom: 40px;
  border-radius: 8px;
  overflow: hidden;
  position: absolute;
  will-change: transform;
  transition: all 0.3s ease-in-out;
  cursor: -webkit-grab;
  cursor: -moz-grab;
  cursor: grab;
`;

const CardMedia = styled.img`
  max-width: 100%;
  pointer-events: none;
`;

const CardMeta = styled.div`
  pointer-events: none;
`;

const CardTitle = styled.div`
  font-size: 20px;
  font-weight: 700;
  pointer-events: none;
`;

const CardSecondary = styled.div`
  font-size: 16px;
  font-weight: 400;
`;

const CardPosition = styled.div`
  font-size: 16px;
  font-weight: 400;
`;

function Card({id, type, wildCard}) {
  const [info, setInfo] = useState({});

  useEffect(() => {
    fetch(`https://statsapi.mlb.com/api/v1/people/${id}?hydrate=currentTeam`)
      .then(response => response.json())
      .then(data => {
        const person = data.people[0];
        setInfo({
          title: person.useName + " " + person.lastName,
          secondary: person.currentTeam.name,
          position: person.primaryPosition.name
        });
      });
  }, [info]);

  return (
    <CardContainer>
      <CardMedia src={`https://img.mlbstatic.com/mlb-photos/image/upload/w_240,h_382,g_auto,c_fill,q_auto:best/v1/people/${id}/action/vertical/current`} />
      <CardMeta>
        <CardTitle>{info.title}</CardTitle>
        <CardSecondary>{info.secondary}</CardSecondary>
        <CardPosition>{info.position}</CardPosition>
      </CardMeta>
    </CardContainer>
  );
}

export default Card;
