import React, { useState } from 'react';
import styled from 'styled-components';

const CardContainer = styled.div`
  display: inline-block;
  width: 90vw;
  max-width: 400px;
  height: 70vh;
  background: #FFFFFF;
  padding-bottom: 40px;
  border-radius: 8px;
  overflow: hidden;
  position: absolute;
  will-change: transform;
  transition: all 0.3s ease-in-out;
  cursor: -webkit-grab;
  cursor: -moz-grab;
  cursor: grab;
`;

const CardMedia = styled.img``;

const CardMeta = styled.div``;

const CardTitle = styled.div``;

function Card({id, type, wildCard}) {
  const [info, setInfo] = useState({});

  fetch(`https://statsapi.mlb.com/api/v1/people/${id}`)
  .then(response => response.json())
  .then(data => {
    setInfo({
      title: data.people[0].useName + data.people[0].lastName
    })
  });

  return (
    <CardContainer>
      <CardMedia src="https://img.mlbstatic.com/mlb-photos/image/upload/w_240,h_382,g_auto,c_fill,q_auto:best/v1/people/594798/action/vertical/current" />
      <CardMeta>
        <CardTitle>{info.title}</CardTitle>
      </CardMeta>
    </CardContainer>
  );
}

export default Card;
